<node TEXT="program to uncompress yaffs image" ID="Freemind_Link_423145255" CREATED="1226750614879" MODIFIED="1226750649321">
<node TEXT="#include &lt;sys/types.h&gt;#include &lt;utime.h&gt;#include &lt;assert.h&gt;#include &lt;stdlib.h&gt;#include &lt;stdio.h&gt;#include &lt;errno.h&gt;#include &lt;string.h&gt;#include &lt;sys/stat.h&gt;#include &lt;unistd.h&gt;#include &lt;sys/mman.h&gt;#define YAFFS_MAX_NAME_LENGTH255#define YAFFS_MAX_ALIAS_LENGTH159#define YAFFS_CHUNCK_DATA_SIZE      2048#define YAFFS_CHUNCK_TAG_SIZE       64typedef struct {unsigned char colParity;unsigned lineParity;unsigned lineParityPrime;} yaffs_ECCOther;typedef struct {unsigned sequenceNumber;unsigned objectId;unsigned chunkId;unsigned byteCount;} yaffs_PackedTags2TagsPart;typedef struct {yaffs_PackedTags2TagsPart t;yaffs_ECCOther ecc;} yaffs_PackedTags2;typedef struct _ChunkWithTag{char data[YAFFS_CHUNCK_DATA_SIZE];yaffs_PackedTags2 pt;char unused[YAFFS_CHUNCK_TAG_SIZE-sizeof(yaffs_PackedTags2)];}ChunkWithTag;typedef enum {YAFFS_OBJECT_TYPE_UNKNOWN,YAFFS_OBJECT_TYPE_FILE,YAFFS_OBJECT_TYPE_SYMLINK,YAFFS_OBJECT_TYPE_DIRECTORY,YAFFS_OBJECT_TYPE_HARDLINK,YAFFS_OBJECT_TYPE_SPECIAL} yaffs_ObjectType;typedef unsigned short __u16;typedef unsigned int   __u32;typedef char YCHAR;typedef struct {yaffs_ObjectType type;int parentObjectId;    __u16 sum__NoLongerUsed;    YCHAR name[YAFFS_MAX_NAME_LENGTH + 1];    __u32 yst_mode; __u32 yst_uid;__u32 yst_gid;__u32 yst_atime;__u32 yst_mtime;__u32 yst_ctime;int fileSize;int equivalentObjectId;YCHAR alias[YAFFS_MAX_ALIAS_LENGTH + 1];__u32 yst_rdev;__u32 roomToGrow[6];__u32 inbandShadowsObject;__u32 inbandIsShrink;__u32 reservedSpace[2];int shadowsObject;__u32 isShrink;} yaffs_ObjectHeader;static void* extract(void* addr, void* end){FILE* fp          = NULL;char cwd[260]     = {0};char* file_name   = NULL;struct utimbuf times = {0};ChunkWithTag* cwt = addr;yaffs_ObjectHeader* obj_hdr = (yaffs_ObjectHeader*)cwt-&gt;data;int parent_id = cwt-&gt;pt.t.objectId;cwt++;getcwd(cwd, sizeof(cwd));//printf(&quot;%s oid=%d\n&quot;, cwd, parent_id);while(((size_t)cwt &lt; (size_t)end)){if(cwt-&gt;pt.t.chunkId == 0){if(fp != NULL){utime(file_name, &amp;times);fclose(fp);fp = NULL;}obj_hdr = (yaffs_ObjectHeader*)cwt-&gt;data;//printf(&quot;%s %s poid=%d(%d) oid=%d\n&quot;, cwd, obj_hdr-&gt;name,//obj_hdr-&gt;parentObjectId, parent_id, cwt-&gt;pt.t.objectId);if(obj_hdr-&gt;parentObjectId != parent_id){char* p = strrchr(cwd, &apos;/&apos;);if(p != NULL){*p = &apos;\0&apos;;}chdir(cwd);return cwt;}switch(obj_hdr-&gt;type){case YAFFS_OBJECT_TYPE_DIRECTORY:{mkdir(obj_hdr-&gt;name, 0777);chdir(obj_hdr-&gt;name);cwt = extract(cwt, end);continue;}case YAFFS_OBJECT_TYPE_FILE:{fp = fopen(obj_hdr-&gt;name, &quot;wb+&quot;);file_name = obj_hdr-&gt;name;times.actime = obj_hdr-&gt;yst_atime;times.modtime = obj_hdr-&gt;yst_mtime;break;}case YAFFS_OBJECT_TYPE_SYMLINK:{if(symlink(obj_hdr-&gt;alias, obj_hdr-&gt;name) != 0){printf(&quot;SYMLINK: %s %s %s fail.\n&quot;, cwd, obj_hdr-&gt;alias, obj_hdr-&gt;name);perror(&quot;symlink&quot;);}break;}case YAFFS_OBJECT_TYPE_HARDLINK:{if(link(obj_hdr-&gt;alias, obj_hdr-&gt;name) != 0){printf(&quot;LINK: %s %s %s fail.\n&quot;, cwd, obj_hdr-&gt;alias, obj_hdr-&gt;name);perror(&quot;link&quot;);}break;}case YAFFS_OBJECT_TYPE_SPECIAL:{int ret = 0;if(S_ISBLK(obj_hdr-&gt;yst_mode)){ret = mknod(obj_hdr-&gt;name, obj_hdr-&gt;yst_mode, obj_hdr-&gt;yst_rdev);}else if(S_ISCHR(obj_hdr-&gt;yst_mode)){ret = mknod(obj_hdr-&gt;name, obj_hdr-&gt;yst_mode, obj_hdr-&gt;yst_rdev);}else if(S_ISFIFO(obj_hdr-&gt;yst_mode)){ret = mknod(obj_hdr-&gt;name, obj_hdr-&gt;yst_mode, obj_hdr-&gt;yst_rdev);}else if(S_ISSOCK(obj_hdr-&gt;yst_mode)){ret = mknod(obj_hdr-&gt;name, obj_hdr-&gt;yst_mode, obj_hdr-&gt;yst_rdev);}else{assert(!&quot;impossible!&quot;);}if(ret != 0){perror(&quot;mknod&quot;);}break;}default:break;}}else{if(fp != NULL){fwrite(cwt-&gt;data, cwt-&gt;pt.t.byteCount, 1, fp);fflush(fp);}}cwt++;}return cwt;}int main(int argc, char* argv[]){void*  addr     = 0;FILE*  fp       = NULL;struct stat st = {0};const char* file_name = argv[1];const char* path = argc == 3 ? argv[2] :&quot;./tmp/&quot;;if(file_name == NULL){printf(&quot;usage: %s image path\n&quot;, argv[0]);return 0;}if((fp = fopen(file_name, &quot;rb&quot;)) == NULL){perror(&quot;fopen&quot;);return 0;}stat(file_name, &amp;st);if((addr = mmap(NULL, st.st_size, PROT_READ, MAP_PRIVATE, fileno(fp), 0)) != NULL){mkdir(path, 0777);chdir(path);extract(addr, (char*)addr + st.st_size);munmap(addr, st.st_size);}else{perror(&quot;mmap&quot;);}fclose(fp);return 0;}" ID="Freemind_Link_906041603" CREATED="1226750585676" MODIFIED="1226750598940"/>
